// SideNavbar.js
import React, { useState } from "react";
import { logo, exit } from "../assets/svgFile";
import TABLE_NAMES from "../constants/constants";
import "./SideNavbar.scss";

const links = [
  { label: "Getting Started", value: "getting-started" },
  { label: "Tables", value: "tables" },
  { label: "Query Editor", value: "query-editor" }
];

const SideNavbar = () => {
  const [showTables, setShowTables] = useState(false);

  const handleTablesClick = () => {
    setShowTables(true);
  };

  const handleCloseTables = () => {
    setShowTables(false);
  };

  return (
    <div className="side-navbar">
      <div className="app-logo">{logo()}</div>
      <hr className="h-line" />
      <div className="nav-links">
        <ul className="link-items">
          {links.map((link) => (
            <li
              key={link.value}
              className={`link-item ${
                "query-editor" === link.value && "active"
              }`}
            >
              {link.value === "tables" ? (
                <button onClick={handleTablesClick}>{link.label}</button>
              ) : (
                link.label
              )}
            </li>
          ))}
        </ul>
      </div>
      <div className="logout">
        <span className="icon">{exit()}</span>
        <span className="text">Logout</span>
      </div>

      {/* Display Tables Modal or Side Panel */}
      {showTables && (
        <div className="tables-modal">
          <div className="tables-container">
            <h2>Available Tables</h2>
            <ul>
              {TABLE_NAMES.map((tableName) => (
                <li key={tableName}>{tableName}</li>
              ))}
            </ul>
            <button onClick={handleCloseTables}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default SideNavbar;
