// SideNavbar.js
import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import Modal from "react-modal"; 
import "./SideNavbar.scss";
import logoImage from "../assets/logo-editor.PNG"
import { exit } from "../assets/svgFile";
import TABLE_NAMES from "../constants/constants";

const links = [
  { label: "Getting Started", value: "getting-started", path: "/" },
  { label: "Tables", value: "tables", path: "/tables" },
  { label: "Query Editor", value: "query-editor", path: "/query-editor" }
];

const SideNavbar = () => {
  const location = useLocation();
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  return (
    <div className="side-navbar">
      <div className="app-logo"><img src={logoImage} alt="Logo" style={{width: "150px", height: "150px" }} /> </div>
      <hr className="h-line" />
      <div className="nav-links">
        <ul className="link-items">
          
          {links.map((link) => (
            <li
              key={link.value}
              className={`link-item ${location.pathname === link.path && "active"}`}
            >
              {link.value === "tables" ? (
                // Open modal for "Tables" link
                <button onClick={openModal}>{link.label}</button>
              ) : (
                // Use Link for other links
                <Link to={link.path}>{link.label}</Link>
              )}
            </li>
          ))}
        </ul>
      </div>
      <div className="logout">
        <span className="icon">{exit()}</span>
        <span className="text">Logout</span>
      </div>

      {/* Modal for "Tables" link */}
      <Modal isOpen={isModalOpen} onRequestClose={closeModal} className="custom-modal">
        <h2>Table List</h2>
        <ul>
          {TABLE_NAMES.map((tableName) => (
            <li key={tableName}>{tableName}</li>
          ))}
        </ul>
        <button onClick={closeModal}>Close Modal</button>
      </Modal>
    </div>
  );
};

export default SideNavbar;
