// SideNavbar.js
import React, { useState } from "react";
import Modal from "react-modal";
import { exit, logo } from "../assets/svgFile";

const links = [
  { label: "Getting Started", value: "getting-started" },
  { label: "Tables", value: "tables" },
  { label: "Query Editor", value: "query-editor" }
];

const SideNavbar = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="side-navbar">
      <div className="app-logo">{logo()}</div>
      <hr className="h-line" />
      <div className="nav-links">
        <ul className="link-items">
          {links.map((link) => (
            <li
              key={link.value}
              className={`link-item ${
                "query-editor" === link.value && "active"
              }`}
              onClick={link.value === "tables" ? openModal : undefined}
            >
              {link.label}
            </li>
          ))}
        </ul>
      </div>
      <div className="logout">
        <span className="icon">{exit()}</span>
        <span className="text">Logout</span>
      </div>

      {/* Modal for Tables */}
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Tables Modal"
      >
        <h2>Available Tables</h2>
        <ul>
          {/* Render your table names here */}
        </ul>
        <button onClick={closeModal}>Close Modal</button>
      </Modal>
    </div>
  );
};

export default SideNavbar;
